class Solution:    def addStrings(self, num1: str, num2: str) -> str:        value1 = 0        value2 = 0        for idx, item in enumerate(num1):            value1 += pow(10, len(num1) - idx - 1) * (ord(item) - ord('0'))        for idx, item in enumerate(num2):            value2 += pow(10, len(num2) - idx - 1) * (ord(item) - ord('0'))        add_result = str(value1 + value2)        return add_result